function gotoNext(){quizFinished?axios.get(`/api/getQuestion?id=${model.quizId}&page=${page}`).then(function(n){$("#questionContent").html(n.data);page++}).catch(n=>console.log(n)):axios.get(`/api/getQuizSummary?id=${model.quizId}`).then(function(n){$("#questionContent").html(n.data)}).catch(n=>console.log(n))}function answerQuestion(n){guessBtn=n.target;axios.get(util.apiUrl("/api/answer",{quizId:model.quizId,questionId:$("#question").attr("value"),guess:n.target.value})).then(function(n){let t=n.data.correctAnswer;quizFinished=n.data.quizFinished;console.log(quizFinished,n);let i=$("#question").find(`[value='${t}']`)[0];$(i).css("background-color","#28a745");guessBtn.value!==t&&$(guessBtn).css("background-color","#dc3545")})}let quizFinished=!1,page=0,guessBtn;gotoNext();$("#nextBtn").click(gotoNext);$(document).on("click","#question .btn",answerQuestion);